package org.erosion2020.util;

import javax.crypto.Cipher;
import javax.crypto.NoSuchPaddingException;
import javax.crypto.spec.SecretKeySpec;
import java.io.File;
import java.security.NoSuchAlgorithmException;
import java.util.Base64;

public class BehinderUtil {
    public static byte[] deAes(String key, String cipherText) throws Exception {
        Cipher cipher = Cipher.getInstance("AES");
        cipher.init(Cipher.DECRYPT_MODE, new SecretKeySpec(key.getBytes(), "AES"));

        byte[] classBytes = cipher.doFinal(Base64.getDecoder().decode(cipherText));
        return classBytes;
    }

    public static void main(String[] args) throws Exception {
        byte[] bytes = deAes("76a2173be6393254",
                "");
        ClassFiles.bytes2File(bytes, new File("C:\\Users\\erosi\\Desktop\\bx.class"));
    }
}
